        Question(text: "Czy jesteś w stanie teraz odmówić czegoś, czego nie chcesz robić, choć czujesz presję?",subtitle: "Umiejętność stawiania granic to wyzwanie na co dzień.",themeColor:"Black", category: "wyzwania", image: "strong", backgroundColor: "wyzwania"),
        Question(text: "Zaplanuj wyprawę w nieznane miejsce. Gdzie pojedziesz?",subtitle: "Podróże to często sposób na wyjście z rutyny.",themeColor:"Black", category: "wyzwania", image: "strong", backgroundColor: "wyzwania"),
        Question(text: "Wybierz jedną osobę, której wybaczysz dawne krzywdy. Jesteś gotowy(-a)?",subtitle: "Przebaczenie to dar przede wszystkim dla samego siebie.",themeColor:"Black", category: "wyzwania", image: "strong", backgroundColor: "wyzwania"),
        Question(text: "Podejmij próbę zmiany jednego złego nawyku przez kolejny tydzień. Który wybierasz?",subtitle: "Systematyczna praca nad sobą przynosi wymierne korzyści.",themeColor:"Black", category: "wyzwania", image: "strong", backgroundColor: "wyzwania"),
        Question(text: "Poświęć dziś 15 minut na medytację lub ciszę. Spróbujesz?",subtitle: "Cisza bywa źródłem inspiracji i wewnętrznego spokoju.",themeColor:"Black", category: "wyzwania", image: "strong", backgroundColor: "wyzwania"),
        Question(text: "Napisz list do przyszłego siebie. Zrobisz to?",subtitle: "Perspektywa czasu odkrywa, jak daleko zaszliśmy.",themeColor:"Black", category: "wyzwania", image: "strong", backgroundColor: "wyzwania"),
        Question(text: "Podejmij wyzwanie: jeden dzień z dietą, której nigdy nie próbowałeś(-aś). Jaką wybierasz?",subtitle: "Nowe doświadczenia wzbogacają codzienność.",themeColor:"Black", category: "wyzwania", image: "strong", backgroundColor: "wyzwania"),
        Question(text: "Przez tydzień zwracaj uwagę na to, by mówić o innych wyłącznie pozytywnie. Potrafisz?",subtitle: "Chwalenie innych zmienia także nasze nastawienie.",themeColor:"Black", category: "wyzwania", image: "strong", backgroundColor: "wyzwania"),
        Question(text: "Zrób coś, co zawsze odkładałeś(-aś) na później. Co to będzie?",subtitle: "Prokrastynacja potrafi nas ograniczać, przełam ją.",themeColor:"Black", category: "wyzwania", image: "strong", backgroundColor: "wyzwania"),
        Question(text: "Spróbuj nowego sportu lub aktywności fizycznej. Masz pomysł, co to będzie?",subtitle: "Ruch wyzwala endorfiny i energię do działania.",themeColor:"Black", category: "wyzwania", image: "strong", backgroundColor: "wyzwania"),
        Question(text: "Czy odważysz się poprosić kogoś o pomoc w trudnej sprawie?",subtitle: "Proszenie o pomoc to nie słabość, lecz wyraz odwagi.",themeColor:"Black", category: "wyzwania", image: "strong", backgroundColor: "wyzwania"),
        Question(text: "Powstrzymaj się od krytyki kogokolwiek przez jeden dzień. Podołasz?",subtitle: "Świadomość słów to krok do lepszej empatii.",themeColor:"Black", category: "wyzwania", image: "strong", backgroundColor: "wyzwania"),
        Question(text: "Zrób spontaniczny gest życzliwości wobec nieznajomego. Co to będzie?",subtitle: "Dobro rozgrzewa serca obu stron.",themeColor:"Black", category: "wyzwania", image: "strong", backgroundColor: "wyzwania"),
        Question(text: "Wybierz jedną rzecz, która Cię przeraża, i zacznij dziś pracę nad jej pokonaniem. Zrobisz to?",subtitle: "Strach jest sygnałem, gdzie leży potencjał rozwoju.",themeColor:"Black", category: "wyzwania", image: "strong", backgroundColor: "wyzwania"),
        Question(text: "Podejmij decyzję, którą odkładałeś(-aś) od dawna. Jesteś w stanie zrobić to teraz?",subtitle: "Odkładanie decyzji bywa ciężarem psychicznym.",themeColor:"Black", category: "wyzwania", image: "strong", backgroundColor: "wyzwania"),
        Question(text: "Spróbuj spędzić cały wieczór z wyłączonym telefonem. Gotowy(-a)?",subtitle: "Odłączenie się od sieci może odsłonić nowe pomysły.",themeColor:"Black", category: "wyzwania", image: "strong", backgroundColor: "wyzwania"),
        Question(text: "Wyznaj dzisiaj komuś ważną dla Ciebie prawdę. Odważysz się?",subtitle: "Uwolnienie słów często przynosi głęboki spokój.",themeColor:"Black", category: "wyzwania", image: "strong", backgroundColor: "wyzwania"),
        Question(text: "Zagraj w pantomimę z przyjaciółmi i pokaż najtrudniejsze hasło, jakie sobie wymyślicie!",subtitle: "Głupkowate zabawy rozkręcą każdą imprezę.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Opowiedz nam najśmieszniejszą wpadkę ze swojego życia!",subtitle: "Wspólne śmiechy rozluźniają atmosferę.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Jaką najbardziej zwariowaną rzecz chciałbyś/chciałabyś zrobić na imprezie?",subtitle: "Wyobraźnia to klucz do niezapomnianej zabawy.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Spróbuj naśladować czyjś charakterystyczny śmiech. Potrafisz?",subtitle: "Nic tak nie bawi jak udana parodia.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Masz możliwość stworzyć nowy taneczny krok. Jak byś go nazwał(a)?",subtitle: "Kreatywność taneczna nie ma granic.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Wybierz osobę i powiedz o niej coś zabawnego – ale życzliwego!",subtitle: "Miły humor łączy ludzi, unikajmy docinek.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Gdybyś był(a) postacią z kabaretu, jaki miał(a)byś pseudonim artystyczny?",subtitle: "Czasem absurd w pseudonimach wywołuje najwięcej śmiechu.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Opowiedz najgłupszy dowcip, jaki znasz!",subtitle: "Nawet suchary mogą rozbawić, jeśli są dobrze opowiedziane.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Jak byś zatańczył(-a), gdybyś chciał(a) rozbawić wszystkich na parkiecie?",subtitle: "Odwaga w tańcu zmienia imprezę w show.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Zagrajmy w głuchy telefon – co najzabawniejszego może z tego wyjść?",subtitle: "Zniekształcone szeptem frazy wywołują salwy śmiechu.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Wyobraź sobie, że musisz wykonać piosenkę disco polo przy ludziach. Zrobił(a)byś to?",subtitle: "Czasem spontaniczny występ okazuje się hitem wieczoru.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Czy odważysz się opowiedzieć jakiś wstydliwy żart ze szkoły?",subtitle: "Wspomnienia z dawnych lat mogą być rozbrajająco śmieszne.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Gdyby Twoje życie było komedią, jaki miałaby tytuł?",subtitle: "Autoironia to świetna droga do zdrowego dystansu.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Jakim śmiesznym nawykiem mógłbyś się z nami podzielić?",subtitle: "Nawet drobiazgi z życia mogą bawić do łez.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Czy potrafisz rozśmieszyć wszystkich w pomieszczeniu jednym zdaniem?",subtitle: "Czasem krótkie puenty są najlepsze.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Zdradź swoją najlepszą metodę na szybkie rozładowanie napięcia w grupie!",subtitle: "Imprezowe triki pomagają w integracji.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Wybierz jedną osobę i poproś ją o zaśpiewanie fragmentu ulubionej piosenki. Kogo typujesz?",subtitle: "Śpiew razem daje dużo radości, nawet fałszując!",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Jaki jest Twój ulubiony, dziwaczny taneczny ruch?",subtitle: "Dziwne ruchy na parkiecie to recepta na dobrą zabawę.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Która scena z filmu komediowego najbardziej Cię rozbawiła, i dlaczego?",subtitle: "Wspólne śmianie się z klasyki to rytuał imprezowy.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Czy potrafisz opisać minionkę (postać z bajki) własnymi słowami w śmieszny sposób?",subtitle: "Kreatywne opisy kreskówek potrafią ubawić do łez.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Gdybyś mógł/mogła stworzyć szaloną atrakcję na domówkę, co by to było?",subtitle: "Nietypowe pomysły zapadają w pamięć na długo.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Jaką wymyślną potrawę podał(a)byś na imprezie, aby wszystkich zaskoczyć?",subtitle: "Kuchenne eksperymenty to gwarancja ciekawych reakcji.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Co byś zrobił(-a), gdybyś przypadkiem rozlał(-a) napój na gospodarza?",subtitle: "Z opanowaniem kryzysu rodzą się najlepsze anegdoty.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Jaka była najbardziej kuriozalna sytuacja, w której się znalazłeś(-aś) na imprezie?",subtitle: "Dziwne przygody to sól towarzyskich opowieści.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Czy kiedykolwiek zatańczyłeś(-aś) solo przed lustrem, wyobrażając sobie publiczność?",subtitle: "Prywatne show może być pierwszym krokiem do gwiazdorstwa.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Jeśli miał(a)byś zrobić śmieszną minę do zdjęcia, jak by ona wyglądała?",subtitle: "Najlepsze wspomnienia to często spontaniczne fotki.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Który żart sytuacyjny wywołał u Ciebie największy atak śmiechu?",subtitle: "Zabawne momenty z życia stają się legendami wśród znajomych.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Jaką nietypową umiejętność potrafisz zaprezentować na imprezie?",subtitle: "Czasem ukryte talenty okazują się przebojem wieczoru.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Wyobraź sobie, że jesteś prowadzącym teleturniej komediowy – jakie byłoby pierwsze pytanie?",subtitle: "Kreatywność w zabawie to podstawa.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Gdybyś musiał(a) przebrać się za postać z kreskówki, kogo byś wybrał(a) i czemu?",subtitle: "Przebieranki to doskonały sposób na rozbawienie gości.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Masz do wyboru: powiedzieć coś śmiesznego czy zrobić coś śmiesznego – co wybierasz?",subtitle: "Humor słowny i sytuacyjny to dwie różne, ale komplementarne drogi.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Gdybyś musiał(a) opisać swoje życie piosenką, którą byś wybrał(a)?",subtitle: "Muzyczne skojarzenia rozpalają wyobraźnię.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Co śmiesznego zdarzyło Ci się w szkole i do dziś wspominasz to z uśmiechem?",subtitle: "Wspomnienia z dzieciństwa to często źródło niekończącej się radości.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Kiedy ostatnio śmiałeś(-aś) się tak mocno, że zabrakło Ci tchu?",subtitle: "Intensywny śmiech to najzdrowszy rodzaj terapii.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Wyobraź sobie, że jesteś radiowym spikerem dowcipów. Jak brzmiałaby zapowiedź?",subtitle: "Świat komedii zaczyna się od kreatywnego wstępu.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Jaką przyśpiewkę zaśpiewał(a)byś w gronie przyjaciół, żeby wszystkich rozbawić?",subtitle: "Tradycyjne przyśpiewki mają w sobie ogromne pokłady humoru.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Który z Twoich znajomych najbardziej rozśmiesza Cię samą swoją obecnością?",subtitle: "Otaczanie się wesołymi ludźmi to recepta na dobry nastrój.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "W jaki sposób najchętniej rozkręcasz sztywną imprezę?",subtitle: "Inicjatywa w zabawie bywa kluczem do przełamania lodów.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Wymyśl hasło lub slogan imprezowy, które będzie mottem dzisiejszej zabawy!",subtitle: "Dobre hasło zapada w pamięć na długo.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Jak byś zareagował(-a), gdyby podczas imprezy pojawiła się policja?",subtitle: "Czasem nieoczekiwani goście stają się częścią anegdot.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Zrób najbardziej ekstrawagancki toast, jaki przychodzi Ci do głowy!",subtitle: "Brak granic w toście może wywołać salwy śmiechu.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Jaki jest Twój „guilty pleasure” jeśli chodzi o filmy lub muzykę?",subtitle: "Wstydliwe upodobania to świetny temat do żartów.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Kiedy ostatnio próbowałeś(-aś) namówić kogoś do szalonej zabawy i jak to się skończyło?",subtitle: "Niekiedy najlepsze historie rodzą się z głupich pomysłów.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Jeśli miał(a)byś przyrównać się do jakiegoś mema, który by to był?",subtitle: "Każdy mem to inny rodzaj humoru i autoironii.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Czy potrafisz „strzelić” zabawną gafę celowo, by rozluźnić atmosferę?",subtitle: "Czasem udawany błąd jest lepszy niż najlepszy żart.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Jakie śmieszne przejęzyczenie najbardziej utkwiło Ci w pamięci?",subtitle: "Czasem jedno słowo zmienia całą wypowiedź w zabawną historię.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Którą kreskówkę z dzieciństwa do dziś możesz cytować z pamięci?",subtitle: "Nostalgia połączona z humorem zawsze łączy pokolenia.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Gdybyś stał(a) się bohaterem memów, jakby one brzmiały?",subtitle: "Wyobraźnia memowa nie zna granic, a śmiech jest zaraźliwy.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Czy masz ulubioną anegdotę, którą zawsze opowiadasz nowym znajomym?",subtitle: "Dobre anegdoty przechodzą z ust do ust niczym legendy.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party"),
        Question(text: "Gdybyś został(a) gwiazdą komedii, jaki byłby Twój znak rozpoznawczy?",subtitle: "Każdy komik potrzebuje charakterystycznego wizerunku.",themeColor:"Black", category: "party", image: "party", backgroundColor: "party")
    ]
